		justify-content: center;

		#graphics-container {
			position: relative;
			display: flex;

			justify-content: center;
			align-items: center;

			width: clamp(800px, 45%, 1000px);
			height: auto;
			aspect-ratio: 1/0.9027484144;

			// border: 2px solid blue;

			svg {
				position: absolute;
				width: 100%;
				height: 100%;
			}

			.image-graphics {
				display: block;
				object-fit: cover;

				z-index: 2;

				-webkit-clip-path: url(#clip-path-general);
				clip-path: url(#clip-path-general);

				width: 100%;
				height: 100%;
			}

			.fox {
				position: absolute;
				width: 100%;
				height: 100%;

				transform: translateX(-40px);

				&#fox-black {
					z-index: 1;
				}
				&#fox-white {
					-webkit-clip-path: url(#clip-path-fox);
					clip-path: url(#clip-path-fox);

					z-index: 3;
				}
			}
		}

		#text-container {
			position: relative;
			width: fit-content;
			height: fit-content;
			box-sizing: border-box;

			#statement {
				font-family: "Panchang";

				font-size: 90px;
				line-height: 0.9em;

				color: $black;
				letter-spacing: -2.5px;
			}
			#fake-statement {
